function getSocialIconTitle(url: string): string {
  const lowerUrl = url.toLowerCase();
  if (lowerUrl.includes("github")) return "GitHub";
  if (lowerUrl.includes("twitter") || lowerUrl.includes("x.com")) return "X";
  if (lowerUrl.includes("linkedin")) return "LinkedIn";
  if (lowerUrl.includes("medium")) return "Medium";
  if (lowerUrl.includes("youtube")) return "YouTube";
  if (lowerUrl.includes("instagram")) return "Instagram";
  if (lowerUrl.includes("tiktok")) return "TikTok";
  if (lowerUrl.includes("twitch")) return "Twitch";
  if (lowerUrl.includes("whatsapp")) return "WhatsApp";
  if (lowerUrl.includes("facebook")) return "Facebook";
  if (lowerUrl.includes("amazon")) return "Amazon";
  if (lowerUrl.includes("substack")) return "Substack";
  if (lowerUrl.includes("hashnode")) return "Hashnode";
  if (lowerUrl.includes("gitlab")) return "GitLab";
  if (lowerUrl.includes("leetcode")) return "LeetCode";
  if (lowerUrl.includes("codeforces")) return "Codeforces";
  if (lowerUrl.includes("codechef")) return "CodeChef";
  return "Link";
}

export function SortableDashboardIconLink({
  link,
  onEdit,
  onDelete,
  isDeleting = false,
  isToggling = false,
}: SortableDashboardIconLinkProps) {
  const [isHovered, setIsHovered] = useState(false);
  const {
    attributes,
    listeners,
    setNodeRef,
    transform,
    transition,
    isDragging,
  } = useSortable({ id: link.id });

  const colors = getSocialIconColor(link.url);
  const svgPath = getSocialIconSvg(link.url);
  const linkTitle = getSocialIconTitle(link.url);

  const style = {
    transform: CSS.Transform.toString(transform),
    transition,
    opacity: isDragging ? 0.5 : 1,
  };

  const handleEditClick = (e: React.MouseEvent) => {
    e.preventDefault();
    e.stopPropagation();
    onEdit(link);
  };

  const handleDeleteClick = (e: React.MouseEvent) => {
    e.preventDefault();
    e.stopPropagation();
    onDelete?.(link.id);
  };

  const tooltipText = link.title || linkTitle;

  const iconElement = (
    <div
      {...attributes}
      {...listeners}
      aria-label={tooltipText}
      className="inline-flex items-center justify-center bg-white dark:bg-zinc-100 rounded-xl border border-zinc-200 transition-all hover:scale-105 active:scale-95 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-offset-zinc-100 focus:ring-zinc-400 cursor-grab active:cursor-grabbing"
      style={{
        width: "44px",
        height: "44px",
      }}
    >
      {svgPath ? (
        <svg
          role="img"
          viewBox="0 0 24 24"
          xmlns="http://www.w3.org/2000/svg"
          width="20"
          height="20"
          fill={colors.bg}
          className="shrink-0 pointer-events-none"
        >
          <title>{linkTitle}</title>
          {svgPath}
        </svg>
      ) : link.icon && (link.icon.startsWith("http://") || link.icon.startsWith("https://")) ? (
        <>
          <Image
            src={link.icon}
            alt={linkTitle}
            width={20}
            height={20}
            className="shrink-0 object-contain pointer-events-none"
            unoptimized
            onError={(e) => {
              const target = e.target as HTMLImageElement;
              target.style.display = "none";
              const fallback = target.nextElementSibling as HTMLElement;
              if (fallback) {
                fallback.style.display = "block";
              }
            }}
          />
          <span className="text-lg leading-none hidden pointer-events-none">ðŸ”—</span>
        </>
      ) : (
        <span className="text-lg leading-none pointer-events-none">{link.icon || "ðŸ”—"}</span>
      )}
    </div>
  );

  return (
    <TooltipProvider>
      <div
        ref={setNodeRef}
        style={style}
        className={`relative group shrink-0 ${isDeleting || isToggling ? "opacity-60" : ""} ${isDragging ? "z-50" : ""}`}
        onMouseEnter={() => setIsHovered(true)}
        onMouseLeave={() => setIsHovered(false)}
      >
        <Tooltip>
          <TooltipTrigger render={iconElement as React.ReactElement} />
          <TooltipPopup>{tooltipText}</TooltipPopup>
        </Tooltip>
        {isHovered && (
          <>
            <Tooltip>
              <TooltipTrigger
                render={
                  <Button
                    type="button"
                    size="sm"
                    variant="secondary"
                    className="absolute -top-1 -right-1 h-5 w-5 p-0 rounded-full shadow-sm z-10"
                    onClick={handleEditClick}
                    aria-label={`Edit ${tooltipText}`}
                    disabled={isDeleting || isToggling}
                  >
                    <Pencil className="h-3 w-3" />
                  </Button> as React.ReactElement
                }
              />
              <TooltipPopup>Edit {tooltipText}</TooltipPopup>
            </Tooltip>
            {onDelete && (
              <Tooltip>
                <TooltipTrigger
                  render={
                    <Button
                      type="button"
                      size="sm"
                      variant="secondary"
                      className="absolute -top-1 -left-1 h-5 w-5 p-0 rounded-full shadow-sm z-10 text-destructive hover:text-destructive hover:bg-destructive/10"
                      onClick={handleDeleteClick}
                      aria-label={`Delete ${tooltipText}`}
                      disabled={isDeleting || isToggling}
                    >
                      <Trash2 className="h-3 w-3" />
                    </Button> as React.ReactElement
                  }
                />
                <TooltipPopup>Delete {tooltipText}</TooltipPopup>
              </Tooltip>
            )}
          </>
        )}
      </div>
    </TooltipProvider>
  );
}

